<?phpif(isset($_POST['spa_footer_customization'])){	if($_POST['spa_footer_customization'] == 1)	{	if(!empty($_POST))		{	/*nonce field implement*/			if ( empty($_POST) || !wp_verify_nonce($_POST['spa_footer_nonce_customization'],'spa_customization_nonce_footer') )			{  print 'Sorry, your nonce did not verify.';			   exit;			} else {				$clean=array();				$current_options = get_option('spa_theme_options');				$current_options['footer_tagline']=sanitize_text_field($_POST['footer_tagline']);								update_option('spa_theme_options' , stripslashes_deep($current_options));			}		}	}	// restore defualt data 	if($_POST['spa_footer_customization'] == 2) 	{	$current_options=get_option('spa_theme_options');			$current_options['footer_tagline']='Copyright 2013. All Rights Reserved by';				update_option('spa_theme_options' , $current_options);	}	} ?><div class="block ui-tabs-panel ui-widget-content ui-corner-bottom" id="option_footer" aria-labelledby="ui-id-4" role="tabpanel" style="display: none;" aria-expanded="false" aria-hidden="true">	<input type="hidden" value="Header" name="header">	<form method="post" action = "" style="width:751px;" id="spa_footer_form">				<?php wp_nonce_field('spa_customization_nonce_footer','spa_footer_nonce_customization'); ?> <!--nonce check form-->		<div class="option option-input">			<h3><?php _e('Footer Copyrights','sis_spa');?>  </h3>			<div class="section">				<div class="element">					<input type="text" class="" name="footer_tagline" id="subfooter_left_text" value="<?php if($current_options['footer_tagline']!='') { echo esc_attr($current_options['footer_tagline']); } ?>"/>			   </div>			</div>		</div>          		<input type="hidden" value="1" id="spa_footer_customization" name="spa_footer_customization" /> 		<input type="button" class="button-framework save-options"  value= "<?php _e('Save Changes', 'sis_spa');?>" onclick="datasave_footer()">		<input type="button" class="button-framework reset" value="<?php